import React, { useState } from "react";
import { Button } from "@material-ui/core";
import "./App.css";

function App() {
  const [todos, setTodos] = useState(["Go for a walk", "buy groceries"]);
  const [input, setInput] = useState("");

  const addTodo = event => {
    event.preventDefault();
    setTodos([...todos, input]);
    setInput("");
  };
  return (
    <div className="App">
      <h1>Hello Amitoj Singh</h1>
      <form>
        <input value={input} onChange={event => setInput(event.target.value)} />
        <Button
          disabled={!input}
          type="submit"
          onClick={addTodo}
          variant="contained"
          color="primary"
        >
          Add Todo
        </Button>
      </form>

      <ul>
        {todos.map(todo => (
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
