import React, { useState } from "react";
import "./App.css";

function App() {
  const [todos, setTodos] = useState(["Go for a walk", "buy groceries"]);
  const [input, setInput] = useState("");

  const addTodo = event => {};
  return (
    <div className="App">
      <h1>Hello Amitoj Singh</h1>
      <input value={input} onChange={event => setInput(event.target.value)} />
      <button onClick={addTodo}> Add Todo</button>

      <ul>
        {todos.map(todo => (
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
