import React from "react";
import { BUtton, List, ListItem, ListItemText } from "@material-ui/core";
import "./Todo.css";
import db from "./firebase";

function Todo(props) {
  return (
    <div>
      <List className="todo_list">
        <ListItem>
          <ListItemText primary={props.todo.todo} />
        </ListItem>
        <Button
          onClick={event =>
            db
              .collection("todos")
              .doc(props.todo.id)
              .delete()
          }
        >
          Delete Me
        </Button>
      </List>
    </div>
  );
}

export default Todo;
